import { createSlice, nanoid } from '@reduxjs/toolkit'
import { RootState } from '../../store'
import { Id, IndexOf, Tag } from '../../types';
import { toIndexOf } from '../../utils';

export interface TagState {
  items: IndexOf<Tag>
} 

export const initialState: TagState = {
  items: toIndexOf([
    {
      id: nanoid(),
      label: "First tag",
    },
    {
      id: nanoid(),
      label: "Second"
    }
  ])
}


const slice = createSlice({
  name: 'tags',
  initialState,
  reducers: {

  }
})

export const selectTags = (state: RootState) => Object.values(state.todos.items);

export const selectTagById = (state: RootState, id: Id) =>
  state.tags.items[id]

export const selectTagByValue = (state: RootState, label: string) =>
  Object.values(state.tags).find((tag: Tag) => tag.label === label);

  //export const { postAdded, postUpdated, reactionAdded } = postsSlice.actions

export default slice.reducer
