import { View, StyleSheet } from "react-native"
import { Todo } from "../../types"
import { Card, List, TextInput, Text } from "react-native-paper"
import { useState } from "react"

interface Props {
  item: Todo
}

export const EditTodo = ({item}: Props) => {

  const [label, setLabel] = useState(item.label)

  return (
    <View>
      <TextInput style={style.input} label="Label" value={label} onChangeText={(text) => setLabel(text)}/>
      <MyComponent/>
      <List.Item
        title="First Item"
        description="Item description"
        left={props => <List.Icon {...props} icon="folder" />}
      />
    </View>)
}

const MyComponent = () => (
  <Card mode="contained" style={[style.input, style.card]}>
    <Card.Content style={style.cardContent}>
      <Text variant="labelMedium">Tags</Text>
    </Card.Content>
    <Card.Content style={style.cardContent}>
      <Text variant="labelMedium">Tags</Text>
    </Card.Content>
  </Card>
);

const style = StyleSheet.create({
  input: { 
    margin: 8,
    borderRadius: 4
  },
  card: {
    borderBottomColor: 'rgb(28, 27, 31)',
    borderBottomLeftRadius: 0,
    borderBottomRightRadius: 0,
    borderBottomWidth: 1,
  },
  cardContent: {
    paddingTop: 8
  }

});
