import { View, StyleSheet } from "react-native"
import { Id, Todo } from "../../types"
import { Card, List, TextInput, Text, Chip } from "react-native-paper"
import { useState } from "react"
import { useSelector } from "react-redux"
import { selectTagById } from "../tags/slice"
import { RootState } from "../../store"

interface Props {
  item: Todo
}

export const EditTodo = ({item}: Props) => {

  const [label, setLabel] = useState(item.label)
  const [notes, setNotes] = useState('')

  return (
    <View>
      <TextInput style={style.input} label="Label" value={label} onChangeText={setLabel}/>
      <TagList tags={item.tags}/>
      <TextInput style={style.input} label="Notes" value={notes} onChangeText={setNotes} numberOfLines={8} />
    </View>)
}

interface TagListProps {
  tags: Id[]
}

const TagList = ({ tags }: TagListProps) => {


  return (
    <Card mode="contained" style={[style.input, style.card]}>
      <Card.Content style={style.cardContentTitle}>
        <Text variant="labelMedium">Tags</Text>
      </Card.Content>
      <Card.Content>
        <View style={{ flexDirection:'row' }}>
          {tags.map(tag => <Tag key={tag} id={tag} />)}
          <MyChip
            icon='plus-circle'
            value="Add new tag..."
            onPress={() => console.log('add new')}
          /> 
        </View>
      </Card.Content>
    </Card>
  )
};

interface TagProps {
  id: Id
}

const Tag = ({ id }: TagProps) => {

  const tag = useSelector((state: RootState) => selectTagById(state, id));

  return (
    <MyChip
      value={tag.label}
      onClose={() => { console.log('close') }}
    />
  );
}

interface MyChipProps {
  onClose?: () => void
  onPress?: () => void,
  icon?: string
  value: string
}

const MyChip = (props: MyChipProps) => (  
  <Chip
      { ...props }
      style={{marginRight: 4}}
      compact={true}
      elevated={true}
      mode='outlined'
    >
      {props.value}
  </Chip> 
)

const style = StyleSheet.create({
  input: { 
    margin: 8,
    borderRadius: 4
  },
  card: {
    borderBottomColor: 'rgb(28, 27, 31)',
    borderBottomLeftRadius: 0,
    borderBottomRightRadius: 0,
    borderBottomWidth: 1,
  },
  cardContentTitle: {
    paddingTop: 8,
    marginBottom: 4
  }

});
