import { createSlice, nanoid } from '@reduxjs/toolkit'
import { RootState } from '../../store'
import { Id, Idable, IndexOf, Todo } from '../../types';

const toIndexOf = <T extends Idable>(items: T[]): IndexOf<T> => {
  const result: IndexOf<T> = {};
  items.forEach(item => {
    result[item.id] = item;
  })
  return result;
}

export interface TodoState {
  items: IndexOf<Todo>
} 

const initialState: TodoState = {
  items: toIndexOf([
    {
      id: nanoid(),
      label: "This is the first item",
    },
    {
      id: nanoid(),
      label: "This is the second item"
    },
    {
      id: nanoid(),
      label: "This is the third item"
    }
  ])
}


const slice = createSlice({
  name: 'todos',
  initialState,
  reducers: {

  }
})

export interface TodoFilter {}

export const selectTodos = (state: RootState, filter: TodoFilter = {}) => Object.values(state.todos.items);

export const selectTodoById = (state: RootState, id: Id) =>
  state.todos.items[id]

//export const { postAdded, postUpdated, reactionAdded } = postsSlice.actions

export default slice.reducer
