import { TextInput as PaperTextInput } from "react-native-paper";
import { TextInput as RNTextInput } from "react-native";
import { useState, useRef } from "react";

interface Props {
  placeholder?: string,
  text: string,
  onChangeText: (text: string) => void,
  onEndEditing: (text: string) => void
}

enum FocusBlur {
  Idle = 'idle',
  Focus = 'focus',
  Blur = 'blur'
}

export const TextInput = ({ placeholder, text, onChangeText, onEndEditing }: Props) => {
  const [editing, _setEditing] = useState(false);
  const [valueSnapshot, setValueSnapshot] = useState('');
  const [value, setValue] = useState(text);
  const inputRef = useRef<RNTextInput>(null);

  const setEditing = (state: boolean) => {
    debuggerl
    console.log('setEditing', state);
    _setEditing(state)
  }

  const [debounceFocusBlur, setDebounceFocusBlur] = useState(FocusBlur.Idle);

  const completeFocusBlur = () => {
    setEditing(debounceFocusBlur === FocusBlur.Focus);
    if (debounceFocusBlur === FocusBlur.Blur) {
      onEndEditing(value);
    } else if (debounceFocusBlur === FocusBlur.Focus) {
      setValueSnapshot(value);
    }
    setDebounceFocusBlur(FocusBlur.Idle);
  }

  const setFocusBlur = (state: FocusBlur) => {

    if (debounceFocusBlur === FocusBlur.Idle && state !== FocusBlur.Idle) {
      console.log('setFocusBlur', state)
      setDebounceFocusBlur(state);
      setTimeout(completeFocusBlur, 1)
    } else {
      console.log('setFocusBlur (ignored)', state)
    }
  }

  const startEditing = () => {
    setFocusBlur(FocusBlur.Focus)
  }

  const endEditing = () => {
    setFocusBlur(FocusBlur.Blur)
  }

  return (
    <PaperTextInput
      placeholder={placeholder}
      value={value}
      mode="outlined"
      ref={inputRef}
      outlineStyle={{ borderWidth: 0 }}
      style={{
        backgroundColor: "rgba(0, 0, 0, 0.0)",
        fontSize: 20,
      }}
      onFocus={() => {
        console.log('onFocus');
        startEditing();
      }}
      onBlur={() => {
        console.log('onBlur');
        //setEditing(false);
        endEditing();
      }}
      onChangeText={(text) => {
        console.log('onChangeText');
        setValue(text);
        onChangeText(text);
      }}
      onEndEditing={() => {
        console.log('onEndEditing')
        endEditing();
      }}
      left={
        <PaperTextInput.Icon
          style={{ top: -8 }}
          icon={editing ? "close" : "pencil"}
          forceTextInputFocus={false}
          onPress={
            editing ? () => {

              console.log('onPress.blur');
              setValue(valueSnapshot);
              (inputRef as any).current.blur()
              endEditing();
            } : () => {
              console.log('onPress.focus');
              (inputRef as any).current.focus()
            }
          }
        />
      }
    />
  );
};

TextInput.defaultProps = {
  onChangeText: () => {},
  onEndEditing: () => {},
};

export default TextInput;
