import { View, StyleSheet, Platform, Pressable } from "react-native";
import { IconButton, Text, Chip } from "react-native-paper";

import { Todo } from "../../types";

interface Props {
  item: Todo,
  onItemPress: (id: string) => void
}

const TodoListItem = ({ item, onItemPress }: Props) => {
  const labelOnly = true;//!item.tags?.length;

  return (
    <View key={item.id} style={styles.container}>
      <IconButton icon="checkbox-blank-outline" size={24} />
      <Pressable onPress={() => onItemPress(item.id)}>
        {labelOnly ? (
          <LabelOnly label={item.label} />
        ) : (
          <Composite item={item} />
        )}
      </Pressable>
    </View>
  );
};

TodoListItem.defaultProps = { onItemPress: () => {} };

const Composite = ({ item }: { item: Todo }) => {
  return (
    <View>
      <Text style={{}} variant="titleMedium">
        {item.label}
      </Text>
{/*
      <View style={{ flexDirection: "row" }}>
        {item.tags
          .map(findTag)
          .filter((x) => x)
          .map((tag) => (
            <Chip
              key={tag.label}
              style={{ marginHorizontal: 4 }}
              compact={true}
              elevated={true}
            >
              {tag.label}
            </Chip>
          ))}
      </View>
          */}
    </View>
  );
};

const LabelOnly = ({ label }: { label: string }) => (
  <View style={styles.labelOnlyTextContainer}>
    <Text style={styles.labelOnlyText} variant="titleMedium">
      {label}
    </Text>
  </View>
);

const styles = StyleSheet.create({
  container: {
    flexDirection: "row",
    marginTop: 16,
    borderBottomColor: "grey",
    borderStyle: "dotted",
    borderBottomWidth: 1,
    paddingBottom: 16,
  },
  labelOnlyTextContainer: Object.assign(
    {
      flexDirection: "column",
      flex: 1,
    },
    Platform.OS === "web" ? { paddingTop: 14 } : {}
  ),
  labelOnlyText: { flex: 1, verticalAlign: "middle" },
} as any);

export default TodoListItem;
