import { View, Text } from 'react-native';
import { Screens } from '../types'
import type { Id, Screen, ScreenProps } from '../types';
import { selectTodoById } from '../features/todos/slice';
import { RootState } from '../store';
import { useSelector } from 'react-redux';

interface TodoDetailsScreenProps {
  id: Id
}


export const TodoDetailsScreen = ({route}: ScreenProps<TodoDetailsScreenProps>) => {
  const { id } = route.params;
  const todo = useSelector((state: RootState) => selectTodoById(state, id))

  return (
    <>
    <View><Text>Todo Details Screen ({JSON.stringify(todo)})</Text></View>
    </>
  )
}

const screen: Screen = {
  name: Screens.TodoDetails,
  screen: TodoDetailsScreen
}

export default screen;
